import {
  ADD_EXPENSE,
  ADD_BALANCE,
  EDIT_EXPENSE,
  DELETE_EXPENSE,
} from "../actions/appActions";

const initialState = {
  expensesList: [],
  totalBudget: 0,
  expenses: 0,
};

const appReducer = (state = initialState, action) => {
    switch (action.type) {
      case ADD_EXPENSE:
        return {
          ...state,
          totalBudget: state.totalBudget - action.payload.expense,
          expenses: state.expenses + action.payload.expense,
          expensesList: [...state.expensesList, action.payload],
        };
      case ADD_BALANCE:
        return {
          ...state,
          totalBudget: state.totalBudget + action.payload.balance,
        };
      case EDIT_EXPENSE:
        const oldExpense = state.expensesList.find(expense => expense.id === action.payload.id);
        return {
          ...state,
          totalBudget: state.totalBudget + oldExpense.expense - action.payload.expense,
          expenses: state.expenses - oldExpense.expense + action.payload.expense,
          expensesList: state.expensesList.map(expense => 
            expense.id === action.payload.id ? action.payload : expense),
        };
      case DELETE_EXPENSE:
        const expenseToDelete = state.expensesList.find(expense => expense.id === action.payload.id);
        return {
          ...state,
          totalBudget: state.totalBudget + expenseToDelete.expense,
          expenses: state.expenses - expenseToDelete.expense,
          expensesList: state.expensesList.filter(expense => expense.id !== action.payload.id),
        };
      default:
        return state;
    }
  };
  
  export default appReducer;
