import { products } from "../data"
export const ADD_PRODUCT = 'ADD_PRODUCT'




export const add = (productId) => {
    const product = products.find((p) => p.id === productId);
    
    return {
      type: ADD_PRODUCT,
      payload: {
        id: product.id,
        quantity: 1
      }
    }
  }
